mapper.update:
UPDATE notices
SET  title = #{title},
     content = #{content}
WHERE notices_id = #{notices_id}

UPDATE notices
SET  isdelete = 1
WHERE notices_id = #{notices_id}

UPDATE faqs
SET category = #{category},
    title = #{title},
    ask = #{ask},
    comment = #{comment}
WHERE faqs_id = #{faqs_id}

UPDATE faqs
SET  isdelete = 1
WHERE faqs_id = #{faqs_id}

UPDATE download
SET category = #{category},
    title = #{title},
    content = #{content}
WHERE downloads_id = #{downloads_id}

UPDATE download
SET  isdelete = 1
WHERE downloads_id = #{downloads_id}
